import { Component, OnInit,NgModule } from '@angular/core';


import { Product } from '../product.model';
import { AngularFirestore } from '@angular/fire/firestore';
import { FormBuilder,FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../product.service';
import { Observable } from 'rxjs';
import { ProductCreateComponent } from '../product-create/product-create.component';


@Component({
  selector: 'app-product-read-cloud',
  templateUrl: './product-read-cloud.component.html',
  styleUrls: ['./product-read-cloud.component.css']
})
export class ProductReadCloudComponent implements OnInit {
Produtos: any[]=[];
items!:Observable<any[]>;

products: Product[]=[]
displayedColumns = ['id', 'name', 'price', 'color', 'action'];


constructor(private produtoService: ProductService,
            private firestore: AngularFirestore, 
          ){
  this.items = firestore.collection('Items').valueChanges();


}

ObterProduto(){
  this.produtoService.getProduto().subscribe(data=>{
    data.forEach((element:any) =>{
    //console.log(element.payload.doc.id);
  //console.log(element.payload.doc.data());  
this.Produtos.push({
  id: element.payload.doc.id,
  ...element.payload.doc.data()
}) 
});

console.log(this.Produtos)
  });


};




ngOnInit():void{
 this.ObterProduto()
}

}
